import React, { useContext, useEffect } from 'react'
import {useState } from 'react';
import { GlobalStoreContext } from '../store'
import ListCard from './ListCard.js'
import { Fab, Typography } from '@mui/material'
import List from '@mui/material/List';
import DeleteModal from './DeleteModal';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import MenuItem from '@mui/material/MenuItem';
import Menu from '@mui/material/Menu';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import TextField from '@mui/material/TextField';
import AppBar from '@mui/material/AppBar';
import Home from '@mui/icons-material/HomeOutlined';
import Group from '@mui/icons-material/GroupsOutlined';
import Person from '@mui/icons-material/PersonOutline';
import Sigma from '@mui/icons-material/FunctionsOutlined';
/*
    This React component lists all the top5 lists in the UI.

    @author McKilla Gorilla
*/
const HomeScreen = () => {
    const { store } = useContext(GlobalStoreContext);
    const [anchorEl, setAnchorEl] = useState(null);
    const isMenuOpen = Boolean(anchorEl);

    useEffect(() => {
        store.loadIdNamePairs();
    }, []);

    const handleProfileMenuOpen = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleMenuClose = () => {
        setAnchorEl(null);
    };

    let listCard = "";
    if (store) {
        listCard = 
            <List sx={{ width: '90%', left: '5%', bgcolor: 'background.paper' }}>
            {
                store.idNamePairs.map((pair) => (
                    <ListCard
                        key={pair._id}
                        idNamePair={pair}
                        selected={false}
                    />
                ))
            }
            </List>;
    }
    return (
        <div id="top5-list-selector">
            <div id="list-selector-heading">
            <AppBar position="static">
                <Toolbar sx={{bgcolor:"#e0e0e0"}}>
                    <Box sx={{ display: { xs: 'none', md: 'flex' } }}>
                        <IconButton>
                            <Home></Home>
                        </IconButton>
                        <IconButton>
                            <Group></Group>
                        </IconButton>
                        <IconButton>
                            <Person></Person>
                        </IconButton>
                        <IconButton>
                            <Sigma></Sigma>
                        </IconButton>
                        <TextField fullWidth ></TextField>
                    </Box>
                </Toolbar>
            </AppBar>
            <Menu
                anchorEl={anchorEl}
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right',
                }}
                open={isMenuOpen}
                onClose={handleMenuClose}>
                    <MenuItem>Published Date (Newest)</MenuItem>
                    <MenuItem>Published Date (Oldest)</MenuItem>
                    <MenuItem>Views</MenuItem>
                    <MenuItem>Likes</MenuItem>
                    <MenuItem>Dislikes</MenuItem>
                    SORTED BY
                </Menu>
            </div>
            <div id="list-selector-list">
                {
                    listCard
                }
                <DeleteModal />
            </div>
        </div>)
}

export default HomeScreen;
