import React from "react";
import Box from '@mui/material/Box';
import { useContext } from 'react';
import { GlobalStoreContext } from '../store';

export default function Sidebar () {
    const {store}=useContext(GlobalStoreContext);
    function handleClick(event,input){
        event.stopPropagation();
        store.setMode(input);
    }
        return (
            <div id="sidebar">
                <div id="sidebar-menu">
                <Box class="menu" onClick={(event) => {handleClick(event,"friends")}}>Friends</Box>
                <Box class="menu" onClick={(event) => {handleClick(event,"followings")}}>Followings</Box>
                <Box class="menu" onClick={(event) => {handleClick(event,"works")}}>Works</Box>
                <Box class="menu" onClick={(event) => {handleClick(event,"library")}}>Library</Box>
                <Box class="menu" onClick={(event) => {handleClick(event,"likes")}}>Your Likes</Box>
                </div>
            </div>
        );

}
