import { useContext } from 'react'
import Top5Item from './Top5Item.js'
import List from '@mui/material/List';
import { Typography } from '@mui/material'
import { GlobalStoreContext } from '../store/index.js'
import * as React from 'react';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import AuthContext from '../auth/';


/*
    This React component lets us edit a loaded list, which only
    happens when we are on the proper route.

    @author McKilla Gorilla
*/
function WorkspaceScreen() {
    const { store } = useContext(GlobalStoreContext);
    const auth = useContext(AuthContext);
    function handleKeyPress (event) {
        event.preventDefault();
        const formData = new FormData(event.currentTarget);
        let comment=formData.get('comment');
        let author=auth.user.firstName+" "+auth.user.lastName;
        let payload={author:author,comment:comment}
        store.comment(payload);
    }
    let editItems = "";
    let comment = "";
    if (store.currentList) {
        editItems = (
        <Box id = "workspace-edit">  
            <Box id="edit-numbering">
            {store.currentList.items.map((item) => (
                <Box className='item-number'sx={{flexDirection:"column",color:'#d4af36', bgcolor: 'inherit'}}>
                    {(store.currentList.items.indexOf(item)+1)+". "+store.currentList.items[store.currentList.items.indexOf(item)]}
                </Box>
            ))}
            </Box>
        </Box>  
        );
        comment = (
        <Box id = "workspace-edit">  
            <Box id="edit-numbering">
            {store.currentList.comment.map((text) => (
                <Box className='item-number'sx={{flexDirection:"column",color:'black', bgcolor: '#d4af36'}}>
                    <Box sx={{color:'blue'}}>{text.author}</Box>
                    <Box>{text.comment}</Box>
                </Box>
            ))}
            <TextField sx={{bgcolor:'white'}} label="add comment..." 
            onKeyPress={(event) => {
                handleKeyPress(event)
            }}
            name="comment"
            id="comment"></TextField>
            </Box>
        </Box>  
        );
    }
    return (
        <div id="top5-workspace">
            <div id="workspace-edit">
                {editItems}
                {comment}
            </div>
        </div>
    )

}

export default WorkspaceScreen;
